int testOne()
{
	DLList L;
	L = newDLList();
	assert(validDLList(L));
	assert(DLListMove(L,100) == 1);
	assert(validDLList(L));
	DLListBefore(L, "thisstring Goes here1");
	DLListBefore(L, "thisstring Goes here1111");
	DLListBefore(L, "thisstring Goes here1.1");
	DLListBefore(L, "thisstring Goes here1.1.1.");
	DLListBefore(L, "thisstring Goes here1.1.1.1.");
	DLListBefore(L, "thisstring Goes here1.1.1.1.1.");
	assert(DLListMove(L,-100) == 1);
	assert(validDLList(L));
	showDLList(stdout, L);
	DLListAfter(L, "");
	DLListBefore(L, "");
	assert(validDLList(L));
	showDLList(stdout, L);
	assert(DLListLength(L) == 8);
	DLListDelete(L);
	DLListDelete(L);
	DLListDelete(L);
	DLListDelete(L);
	DLListDelete(L);
	DLListDelete(L);
	DLListDelete(L);
	DLListDelete(L);
	assert(DLListIsEmpty(L) == 1);
	assert(validDLList(L));
	showDLList(stdout, L);
	fprintf(stdout, "CONGRATZ YOU PASSED TEST ONE\n");

	return 1;
}

